let progress=document.getElementById("progressbar"),totalHight=document.body.scrollHeight-window.innerHeight;window.onscroll=function(){let e=window.pageYOffset/totalHight*100;progress.style.height=e+"%"};const cursor=document.querySelector(".cursor");document.addEventListener("mousemove",e=>{cursor.setAttribute("style","top: "+(e.pageY-10)+"px; left: "+(e.pageX-10)+"px;")}),document.addEventListener("click",()=>{cursor.classList.add("expand"),setTimeout(()=>{cursor.classList.remove("expand")},500)});
